    const result = /^rgba?\((\d+),\s*(\d+),\s*(\d+)/i.exec(rgb);
    if (!result) return null;
    return `#${
      (+result[1] < 16 ? '0' : '') + parseInt(result[1]).toString(16) +
      (+result[2] < 16 ? '0' : '') + parseInt(result[2]).toString(16) +
      (+result[3] < 16 ? '0' : '') + parseInt(result[3]).toString(16)
    }`.toUpperCase();
  }

  // Check if string is a 6-character hex color
  function isHexColor(str) {
    return /^#[0-9A-F]{6}$/i.test(str);
  }

  const colorCountMap = new Map();

  function addColor(color) {
    let hexColor = null;
    if (color.startsWith('rgb')) {
      hexColor = rgbToHex(color);
    } else if (color.startsWith('#')) {
      hexColor = color.toUpperCase();
      if (hexColor.length === 4) {
        // convert 3 digit hex to 6 digit
        hexColor = '#' + hexColor[1] + hexColor[1] + hexColor[2] + hexColor[2] + hexColor[3] + hexColor[3];
      }
    }
    // Only add valid 6 char hex colors
    if (hexColor && isHexColor(hexColor)) {
      if (colorCountMap.has(hexColor)) {
        colorCountMap.set(hexColor, colorCountMap.get(hexColor) + 1);
      } else {
        colorCountMap.set(hexColor, 1);
      }
    }
  }

  const allElements = document.querySelectorAll('*');
  allElements.forEach(el => {
    const style = getComputedStyle(el);
    ['color', 'backgroundColor', 'borderTopColor', 'borderRightColor', 'borderBottomColor', 'borderLeftColor'].forEach(prop => {
      const value = style[prop];
      if (value && value !== 'rgba(0, 0, 0, 0)' && value !== 'transparent' && value !== 'initial') {
        addColor(value);
      }
    });
  });

  const svgElements = document.querySelectorAll('svg *');
  svgElements.forEach(svgEl => {
    ['fill', 'stroke'].forEach(attr => {
      const val = svgEl.getAttribute(attr);
      if (val && val !== 'none' && !val.startsWith('url(')) {
        addColor(val);
      }
    });
  });

  const sortedColors = [...colorCountMap.entries()].sort((a, b) => b[1] - a[1]);
  return sortedColors.slice(0, 10).map(([color]) => color);
})()
